  nickName?: string;
  dateOfBirth?: Date;
  validId?: string;
  emailAddress?: string;
  mobileNumber?: string;
  verified: boolean;
  verificationStatus: MemberAccountVerificationStatus;
  mobileNumberVerified: boolean;
